mapper.update:
update sku_info
set lock_stock = lock_stock + #{skuNum}
where id = #{skuId}

update sku_info
set lock_stock = lock_stock - #{skuNum}
where id = #{skuId}

update sku_info
set stock      = stock - #{skuNum},
    lock_stock = lock_stock - #{skuNum},
    sale       = sale + #{skuNum}
where id = #{skuId}
